import moment from 'moment'
import { curry } from 'ramda'

export const formatDate = dateValue =>
  moment(dateValue).format('MMM DD hh:mm:ss')

export const rangeInRange = curry((start, end, start2, end2) => {
  const s = moment(start)
  const e = moment(end)
  return moment(start2).isBetween(s, e) && moment(end2).isBetween(s, e)
})
